import React, { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  CardFooter,
  Row,
  Col,
  Spinner,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
} from "reactstrap";
import SlidesService from "services/slides.service";
import "assets/css/course-gallery.css";

const CourseGallery = () => {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchCourses();
  }, []);

  useEffect(() => {
    filterCourses();
  }, [searchTerm, courses]);

  const fetchCourses = async () => {
    try {
      const data = await SlidesService.getTestData();
      setCourses(data);
      setFilteredCourses(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching courses:', err);
      setError('Failed to load courses');
    } finally {
      setLoading(false);
    }
  };

  const filterCourses = () => {
    if (!searchTerm.trim()) {
      setFilteredCourses(courses);
      return;
    }

    const filtered = courses.filter(course => 
      course.title.rendered.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.content.rendered.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCourses(filtered);
  };

  if (loading) {
    return (
      <div className="content">
        <div className="text-center">
          <Spinner color="primary" />
          <p>Loading courses...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="content">
        <div className="text-center text-danger">
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Available Courses</CardTitle>
            </CardHeader>
            <CardBody>
              <Row className="mb-4">
                <Col md="6">
                  <InputGroup>
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="fas fa-search" />
                      </InputGroupText>
                    </InputGroupAddon>
                    <Input
                      placeholder="Search courses..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                  </InputGroup>
                </Col>
              </Row>
              <Row>
                {filteredCourses.length === 0 ? (
                  <Col md="12" className="text-center">
                    <p>No courses found matching your search.</p>
                  </Col>
                ) : (
                  filteredCourses.map((course) => (
                    <Col lg="4" md="6" sm="12" key={course.id} className="mb-4">
                      <Card className="card-course">
                        <div className="course-image-container">
                          <img
                            src={course.uagb_featured_image_src?.medium[0] || "https://via.placeholder.com/300x200"}
                            alt={course.title.rendered}
                            className="img-fluid rounded-top"
                            style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                          />
                        </div>
                        <CardBody>
                          <h5 className="card-title">{course.title.rendered}</h5>
                          <div 
                            className="card-text course-description"
                            dangerouslySetInnerHTML={{ 
                              __html: course.content.rendered.substring(0, 150) + '...' 
                            }}
                          />
                        </CardBody>
                        <CardFooter>
                          <div className="d-flex justify-content-between align-items-center">
                            <span className="badge badge-primary">
                              {course.points_amount} Points
                            </span>
                            <a
                              href={course.link}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="btn btn-primary btn-sm"
                            >
                              View Course
                            </a>
                          </div>
                        </CardFooter>
                      </Card>
                    </Col>
                  ))
                )}
              </Row>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CourseGallery;
