import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Button,
  Row,
  Col,
} from "reactstrap";

const SlidesPresentation = ({ slides = [] }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  if (slides.length === 0) {
    return (
      <Card>
        <CardBody>
          <p>No slides available</p>
        </CardBody>
      </Card>
    );
  }

  const currentSlideData = slides[currentSlide];

  return (
    <Card className="card-slide">
      <CardHeader>
        <CardTitle tag="h4">{currentSlideData.title}</CardTitle>
      </CardHeader>
      <CardBody>
        <div className="slides-container">
          <Row>
            <Col md="12" className="text-center mb-4">
              {currentSlideData.image && (
                <div className="slide-image-container">
                  <img
                    src={currentSlideData.image}
                    alt={currentSlideData.title}
                    className="img-fluid rounded"
                    style={{
                      maxHeight: "400px",
                      width: "100%",
                      objectFit: "cover",
                      objectPosition: "center"
                    }}
                  />
                </div>
              )}
            </Col>
            <Col md="12" className="text-center">
              <p className="lead">{currentSlideData.description}</p>
            </Col>
          </Row>
          <div className="d-flex justify-content-between align-items-center mt-4">
            <Button color="primary" onClick={prevSlide}>
              <i className="tim-icons icon-minimal-left" /> Previous
            </Button>
            <span className="slide-counter">
              Slide {currentSlide + 1} of {slides.length}
            </span>
            <Button color="primary" onClick={nextSlide}>
              Next <i className="tim-icons icon-minimal-right" />
            </Button>
          </div>
        </div>
      </CardBody>
    </Card>
  );
};

export default SlidesPresentation;
