import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Button,
  Row,
  Col,
} from "reactstrap";
import { ChevronLeft, ChevronRight } from "react-feather";

const SlidesPresentation = ({ slides = [] }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  if (slides.length === 0) {
    return (
      <Card>
        <CardBody>
          <p>No slides available</p>
        </CardBody>
      </Card>
    );
  }

  const currentSlideData = slides[currentSlide];

  return (
    <Card>
      <CardHeader>
        <CardTitle tag="h4">Presentation</CardTitle>
      </CardHeader>
      <CardBody>
        <div className="slides-container">
          <Row>
            <Col md="12" className="text-center mb-3">
              {currentSlideData.image && (
                <img
                  src={currentSlideData.image}
                  alt={currentSlideData.title}
                  className="img-fluid rounded"
                  style={{ maxHeight: "400px", objectFit: "contain" }}
                />
              )}
            </Col>
            <Col md="12" className="text-center">
              <h3>{currentSlideData.title}</h3>
              <p className="lead">{currentSlideData.description}</p>
            </Col>
          </Row>
          <div className="d-flex justify-content-between mt-4">
            <Button color="primary" onClick={prevSlide}>
              <ChevronLeft size={20} /> Previous
            </Button>
            <span>
              Slide {currentSlide + 1} of {slides.length}
            </span>
            <Button color="primary" onClick={nextSlide}>
              Next <ChevronRight size={20} />
            </Button>
          </div>
        </div>
      </CardBody>
    </Card>
  );
};

export default SlidesPresentation;
